import { useState } from 'react';
import Validations from './Validations';

const url = 'http://localhost:3001/';

function Form() {
  const initialFormData = {
    nome: '',
    email: '',
    cpf: '',
    telefone: '',
    status: 'Ativo'
  };

  const [data, setData] = useState(initialFormData);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      if (Validations(data)) {
        await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(data)
        });
      }
      console.log(data);
      setData(initialFormData);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" name="nome" placeholder="Nome" value={data.nome} onChange={handleChange} />
        <input type="email" name="email" placeholder="E-mail" value={data.email} onChange={handleChange} />
        <input type="text" name="cpf" placeholder="CPF" value={data.cpf} onChange={handleChange} />
        <input type="text" name="telefone" placeholder="Telefone" value={data.telefone} onChange={handleChange} />
        <select name="status" value={data.status} onChange={handleChange}>
          <option value="Ativo">Ativo</option>
          <option value="Inativo">Inativo</option>
          <option value="Aguardando ativação">Aguardando ativação</option>
          <option value="Desativado">Desativado</option>
        </select>
        {Validations(data) ? <> </> : <p>Digite os dados corretamente</p>}
        <input type="submit" value="Criar" />
      </form>
    </div>
  );
}

export default Form;
